import React, { useState, useEffect } from 'react';
import ApiEndpoints from '../../../api/apiEndpoints';
import { useHistory } from 'react-router-dom';

const UserProfilePage = () => {
    const [userData, setUserData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        bio: '',
        phone_number: '',
        image: null // Change to null for better handling of file uploads
    });
    const history = useHistory(); // useHistory hook for navigation

    useEffect(() => {
        // Fetch current user data
        const fetchUserData = async () => {
            try {
                const response = await fetch(ApiEndpoints.PROFILE_URL, {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                    }
                });
                if (response.ok) {
                    const data = await response.json();
                    setUserData(data);
                } else {
                    console.error('Failed to fetch user data:', await response.text());
                }
            } catch (error) {
                console.error('There was an error fetching the user data!', error);
            }
        };

        fetchUserData();
    }, []);

    const handleChange = (event) => {
        setUserData({
            ...userData,
            [event.target.name]: event.target.value,
        });
    };

    const handleFileChange = (event) => {
        setUserData({
            ...userData,
            image: event.target.files[0], // Update the file in state
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('first_name', userData.first_name);
        formData.append('last_name', userData.last_name);
        formData.append('email', userData.email);
        formData.append('bio', userData.bio);
        formData.append('phone_number', userData.phone_number);
        if (userData.image) {
            formData.append('image', userData.image); // Append the file if present
        }

        try {
            const response = await fetch(ApiEndpoints.PROFILE_URL, {
                method: 'PUT',
                body: formData,
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                    // 'Content-Type': 'multipart/form-data' // Fetch automatically sets the correct content type
                }
            });

            if (response.ok) {
                alert('Profile updated successfully!');
            } else {
                console.error('Failed to update profile:', await response.text());
            }
        } catch (error) {
            console.error('There was an error updating the profile!', error);
        }
    };

    return (
        <div>
            <h1>Profile</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    First Name:
                    <input type="text" name="first_name" value={userData.first_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Last Name:
                    <input type="text" name="last_name" value={userData.last_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Email:
                    <input type="email" name="email" value={userData.email} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Bio:
                    <textarea name="bio" value={userData.bio} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Phone Number:
                    <input type="text" name="phone_number" value={userData.phone_number} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Image:
                    <input type="file" name="image" onChange={handleFileChange} />
                </label>
                <br />
                <button type="submit">Update Profile</button>
            </form>
        </div>
    );
};

export default UserProfilePage;
