// src/components/UserProfilePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserProfilePag = () => {
    const [userData, setUserData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        bio: '',
        phone_number: '',
        image: ''
    });

    useEffect(() => {
        // Fetch current user data
        axios.get('/api/profile/', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('access_token')}` // Adjust according to your auth setup
            }
        })
        .then(response => {
            setUserData(response.data);
        })
        .catch(error => {
            console.error('There was an error fetching the user data!', error);
        });
    }, []);

    const handleChange = (event) => {
        setUserData({
            ...userData,
            [event.target.name]: event.target.value,
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.put('/api/profile/', userData, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('access_token')}` // Adjust according to your auth setup
            }
        })
        .then(response => {
            alert('Profile updated successfully!');
        })
        .catch(error => {
            console.error('There was an error updating the profile!', error);
        });
    };

    return (
        <div>
            <h1>Profile</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    First Name:
                    <input type="text" name="first_name" value={userData.first_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Last Name:
                    <input type="text" name="last_name" value={userData.last_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Email:
                    <input type="email" name="email" value={userData.email} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Bio:
                    <textarea name="bio" value={userData.bio} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Phone Number:
                    <input type="text" name="phone_number" value={userData.phone_number} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Image:
                    <input type="file" name="image" onChange={(e) => setUserData({ ...userData, image: e.target.files[0] })} />
                </label>
                <br />
                <button type="submit">Update Profile</button>
            </form>
        </div>
    );
};

export default UserProfilePage;
